import React from "react";
import "./CarCard.css";

/**
 * CarCard component displays individual car information
 * Updated to display seats and handle image URLs for bonus feature
 * @param {Object} props - Component props
 * @param {Object} props.car - The car object to display
 * @param {Function} props.onEdit - Function to call when edit button is clicked
 * @param {Function} props.onDelete - Function to call when delete button is clicked
 */
const CarCard = ({ car, onEdit, onDelete }) => {
  return (
    <div className="car-card">
      {/* Car image with fallback if image URL is not provided - BONUS FEATURE */}
      <div className="car-image">
        {car.imageUrl ? (
          <img src={car.imageUrl} alt={`${car.make} ${car.model}`} />
        ) : (
          <div className="image-placeholder">
            <span>
              {car.make.charAt(0)}
              {car.model.charAt(0)}
            </span>
          </div>
        )}
      </div>

      {/* Car details */}
      <div className="car-details">
        <h3>
          {car.make} {car.model}
        </h3>
        <p>
          <strong>ID:</strong> {car.id}
        </p>
        <p>
          <strong>Seats:</strong> {car.seats}
        </p>

        {/* Action buttons */}
        <div className="car-actions">
          <button
            className="btn-edit"
            onClick={() => onEdit(car)}
            aria-label={`Edit ${car.make} ${car.model}`}
          >
            Edit
          </button>
          <button
            className="btn-delete"
            onClick={() => onDelete(car.id)}
            aria-label={`Delete ${car.make} ${car.model}`}
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default CarCard;
