import React, { useState, useEffect } from "react";
import "./CarForm.css";

/**
 * CarForm component for adding and editing cars
 * Updated to match your server's expected fields (make, model, seats)
 * @param {Object} props - Component props
 * @param {Function} props.onSubmit - Function to call when form is submitted
 * @param {Function} props.onCancel - Function to call when form is cancelled
 * @param {Object} props.initialData - Initial data for editing (null for new cars)
 */
const CarForm = ({ onSubmit, onCancel, initialData }) => {
  // State to manage form data - matches your server's expected fields
  const [formData, setFormData] = useState({
    make: "",
    model: "",
    seats: 5, // Default value
    imageUrl: "", // Bonus field for images
  });

  // State to manage form validation errors
  const [errors, setErrors] = useState({});

  // Effect to populate form when editing an existing car
  useEffect(() => {
    if (initialData) {
      setFormData({
        make: initialData.make || "",
        model: initialData.model || "",
        seats: initialData.seats || 5,
        imageUrl: initialData.imageUrl || "", // Bonus field
      });
    }
  }, [initialData]);

  /**
   * Handles input changes and updates form data
   * @param {Object} e - The change event
   */
  const handleChange = (e) => {
    const { name, value } = e.target;

    // Convert seats to number if needed
    const processedValue = name === "seats" ? parseInt(value) || "" : value;

    setFormData((prev) => ({
      ...prev,
      [name]: processedValue,
    }));

    // Clear error when field is edited
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  /**
   * Validates form data according to your server's requirements
   * @returns {boolean} True if form is valid, false otherwise
   */
  const validateForm = () => {
    const newErrors = {};

    // Your server requires make, model, and seats
    if (!formData.make.trim()) newErrors.make = "Make is required";
    if (!formData.model.trim()) newErrors.model = "Model is required";
    if (!formData.seats || formData.seats < 1 || formData.seats > 20) {
      newErrors.seats = "Please enter a valid number of seats (1-20)";
    }

    // Bonus: Validate image URL format if provided
    if (formData.imageUrl && !isValidUrl(formData.imageUrl)) {
      newErrors.imageUrl = "Please enter a valid URL";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  /**
   * Helper function to validate URLs
   * @param {string} string - The URL to validate
   * @returns {boolean} True if valid URL, false otherwise
   */
  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  /**
   * Handles form submission
   * @param {Object} e - The form submission event
   */
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Convert seats to number before submitting
      const submissionData = {
        ...formData,
        seats: parseInt(formData.seats),
      };

      onSubmit(submissionData);
    }
  };

  return (
    <div className="car-form-container">
      <h2>{initialData ? "Edit Car" : "Add New Car"}</h2>
      <form onSubmit={handleSubmit} className="car-form">
        <div className="form-group">
          <label htmlFor="make">Make *</label>
          <input
            type="text"
            id="make"
            name="make"
            value={formData.make}
            onChange={handleChange}
            className={errors.make ? "error" : ""}
            placeholder="e.g., Toyota, Mercedes-Benz"
          />
          {errors.make && <span className="error-text">{errors.make}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="model">Model *</label>
          <input
            type="text"
            id="model"
            name="model"
            value={formData.model}
            onChange={handleChange}
            className={errors.model ? "error" : ""}
            placeholder="e.g., Corolla, A-class"
          />
          {errors.model && <span className="error-text">{errors.model}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="seats">Seats *</label>
          <input
            type="number"
            id="seats"
            name="seats"
            value={formData.seats}
            onChange={handleChange}
            min="1"
            max="20"
            className={errors.seats ? "error" : ""}
          />
          {errors.seats && <span className="error-text">{errors.seats}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="imageUrl">Image URL (Bonus)</label>
          <input
            type="url"
            id="imageUrl"
            name="imageUrl"
            value={formData.imageUrl}
            onChange={handleChange}
            placeholder="https://example.com/car-image.jpg"
            className={errors.imageUrl ? "error" : ""}
          />
          {errors.imageUrl && (
            <span className="error-text">{errors.imageUrl}</span>
          )}
        </div>

        <div className="form-actions">
          <button type="submit" className="btn-primary">
            {initialData ? "Update Car" : "Add Car"}
          </button>
          <button type="button" onClick={onCancel} className="btn-secondary">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default CarForm;
