import React, { useState, useEffect } from "react";
import { getCars, deleteCar } from "../services/api";
import CarCard from "./CarCard";
import "./CarList.css";

/**
 * CarList component - Displays all cars and handles CRUD operations
 */
const CarList = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    try {
      setLoading(true);
      setError("");
      const carData = await getCars();
      setCars(carData);
    } catch (error) {
      console.error("Error fetching cars:", error);
      setError("Failed to load cars. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCar = async (id) => {
    try {
      await deleteCar(id);
      // Refresh the car list after deletion
      fetchCars();
    } catch (error) {
      console.error("Error deleting car:", error);
      setError("Failed to delete car. Please try again.");
    }
  };

  const handleEditCar = (car) => {
    // For now, just log the edit action
    // You can implement navigation to edit form later
    console.log("Edit car:", car);
    alert(
      `Edit functionality for ${car.make} ${car.model} will be implemented soon!`
    );
  };

  if (loading) {
    return <div className="loading">Loading cars...</div>;
  }

  if (error) {
    return (
      <div className="error-message">
        {error}
        <button onClick={fetchCars}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="car-list-container">
      <div className="car-list-header">
        <h1>Car Inventory</h1>
        <button className="btn btn-primary">Add New Car</button>
      </div>

      <div className="cars-grid">
        {cars.map((car) => (
          <CarCard
            key={car.id}
            car={car}
            onEdit={handleEditCar}
            onDelete={handleDeleteCar}
          />
        ))}
      </div>
    </div>
  );
};

export default CarList;
